INSERT INTO "table" VALUES('cmk8466zu0009cnscpfqjssp0','MBA Marketing Intern Social Media and Admin','Marketing','Kottakkal Kerala','Full-time',replace('Curanova AI is seeking a dynamic MBA Marketing Intern to drive our social media presence and provide crucial administrative support. This role offers a unique opportunity to contribute to a pioneering healthcare AI company focused on genomics, precision medicine, and clinical intelligence.\n\nYou will play a key role in developing and executing engaging social media campaigns, enhancing our digital footprint, and assisting with various administrative functions. This internship provides invaluable hands-on experience in a rapidly evolving HealthTech environment, directly impacting our mission to innovate the future of healthcare.','\n',char(10)),'["Currently enrolled in an MBA program.","Proven experience with social media strategy and content creation for professional brands.","Exceptional written and verbal communication skills with attention to detail.","Proficiency in standard office software (e.g., MS Office Suite, Google Workspace).","Demonstrated passion for healthcare AI, genomics, or HealthTech innovation."]',NULL,NULL,NULL,'PUBLISHED',NULL,1768037993707,1768037997241);
